
public class java08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int a = 10, b = 3;
		if( ++a <= 10 && --b >= 3){
			System.out.println("AA:"+a+"|"+b);
		}else{
			System.out.println("BB:"+a+"|"+b);
		}
	int c =10 , d =3;
		if( ++c <= 10 & --d >= 3){
			System.out.println("CC:"+c+"|"+d);
		}else{
			System.out.println("DD:"+c+"|"+d);
		}
	
	}
}
