import javax.swing.JOptionPane;

public class java11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * Array 物件 = new 
		 * 型態 [] ＝ 名稱
		 */

		int[] a;
		a = new int[4];
		boolean[] b;
		b = new boolean[4];
		for (int i = 0; i < 4; i++) {
			System.out.println(b[i]);
		}
		for(int v:a){
			System.out.println(v);
		}
		
		
	}

}
